import { Pensamento } from './../pensamento';
import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-pensamento',
  templateUrl: './pensamento.component.html',
  styleUrls: ['./pensamento.component.css']
})
export class PensamentoComponent implements OnInit {

  @Input() pensamento: Pensamento = {
    id: 0,
    conteudo: 'I love Angular',
    autoria: 'Nay',
    modelo: 'modelo1'
}

ngOnInit(): void {

}

larguraPensamento(): string {
  if(this.pensamento.conteudo.length >= 256) {
    return 'pensamento-g'
  }
  return 'pensamento-p'
}

}
<div class="pensamento {{ pensamento.modelo }} ff-roboto-mono" [ngClass]="larguraPensamento()">
  <img src="/assets/Imagens/{{ pensamento.modelo }}.png" alt="Aspas coloridas">
  <p class="conteudo"><b>{{ pensamento.conteudo }}</b></p>
  <p class="autorio"><small><i>{{ pensamento.autoria }}</i></small></p>
  <div class="acoes">
    <button class="botao-editar">
      <img src="/assets/Imagens/icone-editar.png" routerLink="/pensamentos/editarPensamento/{{ pensamento.id }}" alt="Ícone de editar">
    </button>
    <button class="botao-excluir" routerLink="/pensamentos/excluirPensamento/{{ pensamento.id }}">
      <img src="/assets/Imagens/icone-excluir.png" alt="Ícone de excluir">
    </button>
  </div>
</div>
